import React from 'react'
import {Form, Button, Label, Error} from '../style/style'


export default function Listing(props) {

    const {onSubmit, values, onChange, errors, disabled} = props

    return(
        <Form >
            <div>
            <h2>Create Listing:</h2>
            </div>

            <Label>Listing Name:&nbsp;
                <input
                    type='text'
                    name='listingname'
                    onChange={onChange}
                    value={values.listingname}
                />
            </Label>
            <Error>{errors.listingname}</Error>

            <Label>Location:&nbsp;
                <input
                    type='text'
                    name='location'
                    value={values.location}
                    onChange={onChange}
                />
            </Label>
            <Error>{errors.location}</Error>

            <Label>Maximum Guests:&nbsp;
                <select
                    name='maxnumguests'
                    value={values.maxnumguests}
                    onChange={onChange}
                />
            </Label>
            <Error>{errors.maxnumguests}</Error>

            <Label>Minimum Stay:&nbsp;
                <input
                    name='minnumnights'
                    onChange={onChange}
                    value={values.minnumnights}
                />
            </Label>
            <Error>{errors.minnumnights}</Error>

            <Label>Beds:&nbsp;
                <input 
                    name='numbeds'
                    onChange={onChange}
                    value={values.numbeds}
                />
            </Label>
            <Error>{errors.password}</Error>

            <h4>Pets Allowed:</h4>
            <Label>Yes
                    <input 
                        checked={values.petsallowed === true}
                        value='true'
                        onChange={onChange}
                        name='petsallowed'
                        type='radio'
                    />
                </Label>
                <Label>No
                <input 
                    checked={values.petsallowed === false}
                    value='false'
                    onChange={onChange}
                    name='petsallowed'
                    type='radio'
                />
                </Label>

            <Button id='submitBtn' disabled={disabled}>Sign Up</Button>
        </Form>
    )
}
